import React from 'react';

import { Routes, Route } from 'react-router-dom';

import './App.css';

import About from './components/About';
import BlogList from './components/BlogList';
import PostDetail from './components/PostDetail';
import PostForm from './components/PostForm';

function App() {
  return (
    <div className="App">
      <Navbar />
      <div className="content">
        <h1>My Personal Blog</h1>
        <Routes>
          <Route path="/" element={<BlogList />} />
          <Route path="/create" element={<PostForm />} />
          <Route path="/post/:id" element={<PostDetail />} />
          <Route path="/about" element={<About />} />
          {/*<Route path="/contact" element={<Contact />} />*/}
          {/* Add routes for auth and policies */}
          <Route path="/login" element={<div><h2>Login Page</h2><p>Implement login form here.</p></div>} />
          <Route path="/signup" element={<div><h2>Signup Page</h2><p>Implement signup form here.</p></div>} />
          <Route path="/profile" element={<div><h2>Profile Page</h2><p>Implement profile view here.</p></div>} />
          <Route path="/policy/privacy-policy" element={<div><h2>Privacy Policy</h2><p>Implement privacy policy content here.</p></div>} />
        </Routes>
      </div>
      <Footer />
    </div>
  );
}

export default App;
