import { setUser } from '@/redux/authSlice'; 
import { ENDPOINTS } from '@/utils/ApiEndPoint'; 

import React from 'react';

import { Link, useNavigate } from 'react-router-dom';

import { useSelector, useDispatch } from 'react-redux';

import ReviewsCarousel from '../ui/ReviewsCarousel'; // Adjust path if needed

const Navbar = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { user } = useSelector((state) => state.auth); // Get user from Redux store

  const handleLogout = async () => {
    try {
      // Assuming you have a logout endpoint
      await fetch(ENDPOINTS.logout, { method: 'POST', credentials: 'include' });
      dispatch(setUser(null)); // Clear user from Redux
      navigate('/login');
    } catch (error) {
      console.error('Logout failed:', error);
    }
  };

  return (
    <nav style={styles.nav}>
      <div style={styles.container}>
        {/* Brand/Logo */}
        <Link to="/" style={styles.logo}>
          MyBlog
        </Link>

        {/* Navigation Links */}
        <ul style={styles.navLinks}>
          <li>
            <Link to="/" style={styles.link}>Home</Link>
          </li>
          <li>
            <Link to="/about" style={styles.link}>About</Link>
          </li>
          <li>
            <Link to="/contact" style={styles.link}>Contact</Link>
          </li>
          {user ? (
            <>
              <li>
                <Link to="/dashboard" style={styles.link}>Dashboard</Link>
              </li>
              <li>
                <button onClick={handleLogout} style={styles.button}>Logout</button>
              </li>
            </>
          ) : (
            <>
              <li>
                <Link to="/login" style={styles.link}>Login</Link>
              </li>
              <li>
                <Link to="/register" style={styles.link}>Register</Link>
              </li>
            </>
          )}
        </ul>
      </div>

      {/* Optional Reviews Carousel */}
      <ReviewsCarousel />
    </nav>
  );
};

// Basic inline styles for simplicity (replace with CSS file or styled-components for production)
const styles = {
  nav: {
    backgroundColor: '#333',
    padding: '1rem 0',
    color: '#fff',
  },
  container: {
    maxWidth: '1200px',
    margin: '0 auto',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '0 20px',
  },
  logo: {
    fontSize: '1.5rem',
    fontWeight: 'bold',
    color: '#fff',
    textDecoration: 'none',
  },
  navLinks: {
    listStyle: 'none',
    display: 'flex',
    gap: '20px',
    margin: 0,
    padding: 0,
  },
  link: {
    color: '#fff',
    textDecoration: 'none',
    fontSize: '1rem',
  },
  button: {
    backgroundColor: '#ff4d4d',
    color: '#fff',
    border: 'none',
    padding: '8px 16px',
    cursor: 'pointer',
    borderRadius: '4px',
  },
};

export default Navbar;
